xpath.script:
$(document).ready(function() {
    $.getJSON('/get_maquinas', function(data) {
        var select = $('', { 'id': 'maquina_selector', 'name': 'maquina_id' });
        $.each(data, function(index, value) {
            select.append($('', {
                value: value.id,
                text: value.name
            }));
        });
        $("#helpdesk_section").append(select);
    });
});
