                          // ----- Utilidades -----
                          function buildBrandHTML(data) {
                            const items = (data.bullets || []).map(b => `
• ${b}`).join("");
                            return `







${data.title || ""}

${data.subtitle || ""}
                                    ${items}







                                    Compatibles con control de usuarios, cuotas y políticas de impresión segura (según configuración).




Recomendado para

Empresas que requieren rendimiento sostenido, seguridad y posibilidades de acabado.



                            `;
                          }

                          function buildProductHTML(data) {
                            const rows = (data.spec || [])
                              .map(([k,v]) => `
${k}${v}`)
                              .join("");
                            return `







${data.title || ""}

${data.desc || ""}







                                      ${rows}




                            `;
                          }

                          function buildBenefitHTML(data) {
                            return `

${data.title || ""}
                              ${data.html || ""}
                            `;
                          }

                          // ----- Delegación de eventos para abrir modales con contenido -----
                          document.addEventListener("click", function (ev) {
                            const card = ev.target.closest("[data-bs-target='#benefitModal'][data-benefit], [data-bs-target='#brandModal'][data-brand], [data-bs-target='#productModal'][data-product]");
                            if (!card) return;

                            // BENEFIT
                            if (card.dataset.benefit) {
                              const key = card.dataset.benefit;
                              const data = benefitContent[key];
                              const titleEl = document.getElementById("benefitModalLabel");
                              const bodyEl  = document.getElementById("benefitModalBody");
                              if (data && titleEl && bodyEl) {
                                titleEl.innerHTML = `${data.title}`;
                                bodyEl.innerHTML  = buildBenefitHTML(data);
                              } else if (bodyEl) {
                                bodyEl.innerHTML = "
Información no disponible.";
                              }
                            }

                            // BRAND
                            if (card.dataset.brand) {
                              const key = card.dataset.brand;
                              const data = brandContent[key];
                              const titleEl = document.getElementById("brandModalLabel");
                              const bodyEl  = document.getElementById("brandModalBody");
                              if (data && titleEl && bodyEl) {
                                titleEl.innerHTML = `${data.title}`;
                                bodyEl.innerHTML  = buildBrandHTML(data);
                              } else if (bodyEl) {
                                bodyEl.innerHTML = "
Información no disponible.";
                              }
                            }

                            // PRODUCT
                            if (card.dataset.product) {
                              const key = card.dataset.product;
                              const data = productContent[key];
                              const titleEl = document.getElementById("productModalLabel");
                              const bodyEl  = document.getElementById("productModalBody");
                              if (data && titleEl && bodyEl) {
                                titleEl.innerHTML = `${data.title}`;
                                bodyEl.innerHTML  = buildProductHTML(data);
                              } else if (bodyEl) {
                                bodyEl.innerHTML = "
Información no disponible.";
                              }
                            }
                          });

                          // Accesibilidad: limpiar contenido al cerrar para evitar foco en elementos ocultos
                          ["benefitModalBody","brandModalBody","productModalBody"].forEach(id=>{
                            const el = document.getElementById(id);
                            const modal = el ? el.closest(".modal") : null;
                            if (!modal) return;
                            modal.addEventListener("hidden.bs.modal", ()=< { if (el) el.innerHTML = ""; });
                          });
                        })();
