template.script:
import { waapi, stagger } from 'https://cdn.jsdelivr.net/npm/animejs@4.0.0-beta.102/+esm';

// Animaciones de entrada suaves
waapi.animate('.animate-card', {
    opacity: [0, 1],
    translateY: ['40px', 0],
    delay: stagger(120),
    duration: 800,
    ease: 'outCubic'
});

// Timeline con efecto de revelado
waapi.animate('.timeline-item', {
    opacity: [0, 1],
    translateX: ['-30px', 0],
    delay: stagger(100, { start: 300 }),
    duration: 600,
    ease: 'outQuart'
});

// Badges con efecto pop
waapi.animate('.badge-animated', {
    scale: [0.5, 1],
    opacity: [0, 1],
    duration: 500,
    ease: 'outBack'
});

// Iconos flotantes
waapi.animate('.float-icon', {
    translateY: [0, -8, 0],
    duration: 2000,
    loop: true,
    ease: 'inOutSine'
});

t.script:
import { waapi } from 'https://cdn.jsdelivr.net/npm/animejs@4.0.0-beta.102/+esm';

waapi.animate('.evaluation-card', {
    opacity: [0, 1],
    scale: [0.95, 1],
    duration: 700,
    ease: 'outBack'
});

waapi.animate('.form-section', {
    opacity: [0, 1],
    translateY: ['20px', 0],
    delay: 200,
    duration: 600,
    ease: 'outCubic'
});

document.addEventListener('DOMContentLoaded', function() {
    const stars = document.querySelectorAll('.star-rating label');
    const ratingLabels = {
        '1': '😞 Muy Malo',
        '2': '😕 Malo',
        '3': '😐 Regular',
        '4': '😊 Bueno',
        '5': '😍 Excelente'
    };
    const ratingLabelElement = document.getElementById('ratingLabel');
    const textarea = document.getElementById('comentario');
    const charCounter = document.getElementById('charCounter');
    const submitBtn = document.getElementById('submitBtn');
    const form = document.getElementById('evaluationForm');

    // Interacción con estrellas
    stars.forEach(star => {
        star.addEventListener('mouseenter', function() {
            const rating = this.getAttribute('data-rating');
            ratingLabelElement.innerHTML = ratingLabels[rating];

            waapi.animate(ratingLabelElement, {
                scale: [0.9, 1],
                opacity: [0.5, 1],
                duration: 300,
                ease: 'outQuad'
            });
        });

        star.addEventListener('click', function() {
            const rating = this.getAttribute('data-rating');
            ratingLabelElement.innerHTML = ratingLabels[rating];

            waapi.animate(this, {
                scale: [1, 1.5, 1.15],
                rotate: [0, 360],
                duration: 600,
                ease: 'outElastic'
            });

            // Habilitar botón de envío
            submitBtn.disabled = false;

            // Animación del botón
            waapi.animate(submitBtn, {
                scale: [1, 1.05, 1],
                duration: 400,
                ease: 'outQuad'
            });
        });
    });

    document.querySelector('.star-rating').addEventListener('mouseleave', function() {
        const checkedStar = document.querySelector('.star-rating input:checked');
        if (!checkedStar) {
            ratingLabelElement.innerHTML = '👆 Selecciona tu calificación';
        }
    });

    // Contador de caracteres
    if (textarea && charCounter) {
        textarea.addEventListener('input', function() {
            const length = this.value.length;
            const max = 500;
            const remaining = max - length;

            charCounter.textContent = `${length} / ${max} caracteres`;

            charCounter.classList.remove('warning', 'danger');
            if (remaining < 100) {
                charCounter.classList.add('warning');
            }
            if (remaining > 20) {
                charCounter.classList.add('danger');
            }
        });
    }

    // Validación en tiempo real
    if (form) {
        form.addEventListener('submit', function(e) {
            const rating = document.querySelector('.star-rating input:checked');

            if (!rating) {
                e.preventDefault();

                // Mostrar error
                ratingLabelElement.innerHTML = '❌ Por favor selecciona una calificación';
                ratingLabelElement.style.color = '#ef4444';

                waapi.animate('.rating-container', {
                    translateX: [-10, 10, -10, 10, 0],
                    duration: 400,
                    ease: 'linear'
                });

                setTimeout(() => {
                    ratingLabelElement.innerHTML = '👆 Selecciona tu calificación';
                    ratingLabelElement.style.color = '';
                }, 2000);

                return false;
            }

            // Animación de envío
            submitBtn.innerHTML = ' Enviando...';
            submitBtn.disabled = true;
        });
    }
});

